			/* // DOM Position key index //
		
			l - Length changing (dropdown)
			f - Filtering input (search)
			t - The Table! (datatable)
			i - Information (records)
			p - Pagination (paging)
			r - pRocessing 
			< and > - div elements
			<"#id" and > - div with an id
			<"class" and > - div with a class
			<"#id.class" and > - div with an id and class
			
			Also see: http://legacy.datatables.net/usage/features
			*/	
	
			/* BASIC ;*/
				var responsiveHelper_datatable_fixed_column = undefined;
				var responsiveHelper_datatable_col_reorder = undefined;
				var responsiveHelper_datatable_tabletools = undefined;
				var title = $('.em-title').text();
				var breakpointDefinition = {
					tablet : 1024,
					phone : 480
				};

			/* COLUMN FILTER  */
		    var otable = $('#datatable_fixed_column').DataTable({
				"order": [[ 0, "desc" ]],
				"sDom": "<'dt-toolbar'<'col-xs-12 col-sm-6'f><'col-sm-6 col-xs-6 hidden-xs'T>r>"+
						"t"+
						"<'dt-toolbar-footer'<'col-sm-6 col-xs-12 hidden-xs'i><'col-sm-6 col-xs-12'p>>",
		        "oTableTools": {
		        	 "aButtons": [
		             "copy",
			            {
			                "sExtends": "csv",
			                "sTitle": title,
			            },		             
			            {
			                "sExtends": "xls",
			                "sTitle": title,
			            },
		                {
		                    "sExtends": "pdf",
		                    "sTitle": title,
		                    "sPdfMessage": title,
		                    "sPdfSize": "letter"
		                },
		             	{
	                    	"sExtends": "print",
	                    	"sMessage": "Generated by "+title+" <i>(press Esc to close)</i>"
	                	}
		             ],
		            "sSwfPath": "/js/plugin/datatables/swf/copy_csv_xls_pdf.swf"
		        },
				"autoWidth" : true,
				"paginate": true,
			"processing": false,
			"serverSide": true,
			ajax: {
				url: '{!! route('datatables.data') !!}',

				type: 'post'
			},
			columns: [
			{ data: 'id', name: 'id' },
			{ data: 'member.mobile', name: 'member.mobile', defaultContent:'' },
			{ data: 'member.id', name: 'member.id', defaultContent:'' },
			{ data: 'member.realname', name: 'member.realname',defaultContent:'' },
			{ data: 'member.prealname', name: 'member.realname', defaultContent:'' },
			{ data: 'product.title', name: 'product.title', defaultContent:'' },
			{ data: 'product.rate', name:'product.rate', searchable: false, render: function (data, type, row) {
					var _msg = " 月";
					if(row.product && row.product.type == 0 ) {
						_msg = " 天";
					}
					return data + "%" +  " /" + _msg;

			}, defaultContent:''},

			{ data: 'member.zhima', name: 'member.zhima', defaultContent:'' },
			{ data: 'money', name: 'money' },
			{ data: 'member.userage', name: 'member.userage', defaultContent:'' },
			{ data: 'created_at', name: 'created_at' },
			{ data: 'id', searchable: false, render: function(data, type, row) {
					var  _url = '{{ url('admin/applysOpt') }}/'+data + '/edit';
					return  '<a class="btn btn-default btn-sm txt-color-pink" href='+ _url + '><span class="fa fa-pencil-square-o"></span> 修改</a>';
			}},

			],

				"preDrawCallback" : function() {
					// Initialize the responsive datatables helper once.
					if (!responsiveHelper_datatable_fixed_column) {
						responsiveHelper_datatable_fixed_column = new ResponsiveDatatablesHelper($('#datatable_fixed_column'), breakpointDefinition);
					}
				},
				"rowCallback" : function(nRow) {
					responsiveHelper_datatable_fixed_column.createExpandIcon(nRow);
				},
				"drawCallback" : function(oSettings) {
					responsiveHelper_datatable_fixed_column.respond();
				}
			
		    });
		    
		    // custom toolbar
		    
		    // Apply the filter
		    $("#datatable_fixed_column thead th input[type=text]").on( 'keyup change', function () {

		        otable
		            .column( $(this).parent().index()+':visible' )
		            .search( this.value )
		            .draw();

		    } );
		    /* END COLUMN FILTER */   